import { computeHeadingLevel } from '@testing-library/dom';
import React, { useState } from 'react';
import './App.css';




function App() {

  const [name, setName] = useState([]);
  const [ele, setEle] = useState('');
  const [clickstate, setClickstate] = useState([]);
  const [selected, setSelected] = useState('');



  function keyChange(e) {
    setEle(e.target.value);
  }

  function handleChange(event) {
    event.preventDefault();
    const namelist= [...name];
    namelist.push(ele);
    setName(namelist);
    setEle('');

 }

 /*function showDropdown() {
  document.getElementById("myDropdown").classList.toggle("show");
}*/

function handlemenu(selected){
  console.log("toggled State:", selected)
  setClickstate[selected] = !clickstate[selected];
  console.log(clickstate);
}

  return (
    <div className="Tasklist">
       <form>
                <div className="header" >
                    <h3>Task list</h3>
                    <input type="text"
                           placeholder="Your Tasks"
                           value={ele}
                           onChange={keyChange}
                           />
                     <button type="submit" onClick={handleChange}>Add</button>
                 </div> 
                 <h6 className="heading_pending">Pending:</h6>
                 <form className="Pendingform">
                 <div className="body">
                      {
                          name.slice(0).reverse().map((_el, i) => {
                            const isSelected = selected === i;
                            return <ul className="card_pending">
                                      <p>{_el}</p>
                                      <button type="button" className="button" onClick={()=>handlemenu(i)}>
                                      â˜°
                                      </button>
                                      {clickstate && 
                                      <div className="dropdown">
                                        <ul>
                                          <li>Edit</li>
                                          <li>Delete</li>
                                        </ul>
                                      </div>
                                      }
                                    </ul>;
                          })
                        }
                 </div>
                </form>
                  <h6 className="heading_Completed">Completed:</h6>
                    <form className="Completedform">
                      <div className="body">
                        <h6>Under baking</h6>
                      </div>
                    </form>
        </form>

    </div>


  );
}

export default App;
