import React, { useState } from 'react';
import './App.css';



function App() {

  function handleKeyDown(event) {
    console.log(event.key);
    if (event.key === 'Enter') {
      console.log('Handled properly');
    }
  }
  return (
    <div className="Tasklist">
       <form>
                <div className="header" >
                    <h1>Task list</h1>
                </div>
                <div className="body">
                    <div className="tasks">
                       <input type="text" 
                              placeholder="Your Tasks"
                              onKeyPress={event =>handleKeyDown(event)}/>
                    </div>
                </div>
        </form>
    </div>

  );
}

export default App;
