import React, { useState } from 'react';
import './App.css';



function App() {

  const [name, setName] = useState([]);

  const handleChange = (e) => {
    console.log(e.target.value);
    setName(e.target.value);

 }

  return (
    <div className="Tasklist">
       <form>
                <div className="header" >
                    <h1>Task list</h1>
                </div>
                <div className="body">
                    <div className="tasks">
			                    <input type="text" placeholder="Your Tasks" onMouseEnter={handleChange} />

                    </div>
                </div>
        </form>
    </div>

  );
}

export default App;
