import { waitForElementToBeRemoved } from '@testing-library/dom';
import { computeHeadingLevel } from '@testing-library/dom';
import React, { useState } from 'react';
import './App.css';




function App() {

  const [name, setName] = useState([]);
  const [ele, setEle] = useState('');
  const [clickstate, setClickstate] = useState([]);



  function keyChange(e) {
    setEle(e.target.value);
  }

  function handleChange(event, i) {
    event.preventDefault();
    const namelist= [...name];
    namelist.push(ele);
    setName(namelist);
    clickstate.push(false);
    setEle('');

 }

 function setClkstate(i) {
  clickstate.push(false);
 }

 /*function showDropdown() {
  document.getElementById("myDropdown").classList.toggle("show");
}*/

function handlemenu(i){
  console.log("toggle index: ", i)
  console.log(clickstate[i]);
  clickstate[i] = !clickstate[i];
  console.log(clickstate[i]);


}

  return (
    <div className="Tasklist">
       <form>
                <div className="header" >
                    <h3>Task list</h3>
                    <input type="text"
                           placeholder="Your Tasks"
                           value={ele}
                           onChange={keyChange}
                           />
                     <button type="submit" onClick={handleChange}>Add</button>
                 </div> 
                 <h6 className="heading_pending">Pending:</h6>
                 <form className="Pendingform">
                 <div className="body">
                      {
                          name.slice(0).reverse().map((_el, i) => {
                            return <div>
                                    <ul className="card_pending">
                                      <p onChange={setClkstate(i)}>{_el}</p>
                                      <button type="button" className="button" onClick={() => handlemenu(i)}>
                                      ☰
                                      </button>
                                    </ul>
                                     if(clickstate[i] == true) { 
                                      <div className="dropdown">
                                        <ul>
                                          <li>Edit</li>
                                          <li>Delete</li>
                                        </ul>
                                      </div>
                                     }

                                      </div>
                          })
                        }
                 </div>
                </form>
                  <h6 className="heading_Completed">Completed:</h6>
                    <form className="Completedform">
                      <div className="body">
                        <h6>Very soon</h6>
                      </div>
                    </form>
        </form>

    </div>


  );
}

export default App;
