import React, { useState, useRef, useEffect} from 'react';
import './App.css';




function App() {
  const wrapperRef = useRef(null);  
  const [name, setName] = useState([]);
  const [ele, setEle] = useState('');
  const [clickstate, setClickstate] = useState([]);

  useEffect(() => {
    document.addEventListener("click", handleClickOutside, false);
    return () => {
      document.removeEventListener("click", handleClickOutside, false);
    };
  }, []);


 const handleClickOutside = event => {
    if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
      console.log("clicked Outside")
      const rstst = [...clickstate]
      rstst.push(false);
      setClickstate(rstst);
    }
  };
  function keyChange(e) {
    setEle(e.target.value);
  }

  function handleChange(event) {
    event.preventDefault();
    const namelist= [...name];
    let input = { key: namelist.length, value: ele}
    namelist.push(input);
    setName(namelist);
    const clkst= [...clickstate]
    clkst.push(false);
    setClickstate(clkst);
    setEle('');

 }


function handlemenu(i){
  const newarr = [...clickstate]
  newarr[i] = !newarr[i];
  setClickstate(newarr);
}

function handledelete(key, i){
  let names = [...name];
  names = names.filter(el => el.key !== key);
  setName(names); 
  const delarr = [...clickstate]
  delarr[i] = !delarr[i];
  setClickstate(delarr);
}

  return (
    <div className="Tasklist" ref={wrapperRef}>
       <form>
                <div className="header" >
                    <h3>Task list</h3>
                    <input type="text"
                           placeholder="Your Tasks"
                           value={ele}
                           onChange={keyChange}
                           />
                     <button type="submit" onClick={handleChange}>Add</button>
                 </div> 
                 <h6 className="heading_pending">Pending:</h6>
                 <form className="Pendingform">
                 <div className="body">
                      {
                          name.slice(0).reverse().map((_el, i) => {
                            return <div>
                                    <ul className="card_pending">
                                      <p>{_el.value}</p>
                                      <button type="button" className="button" onClick={() => handlemenu(i)}>
                                      ☰
                                      </button>
                                    </ul>
                                     {clickstate[i] === true && 
                                      <div className="dropdown">
                                        <ul>
                                          <li>Edit</li>
                                          <li onClick={() => handledelete(_el.key, i)}>Delete</li>
                                        </ul>
                                      </div>
                                      }
                                      </div>
                          })
                        }
                 </div>
                </form>
                  <h6 className="heading_Completed">Completed:</h6>
                    <form className="Completedform">
                      <div className="body">
                        <h6>Very soon</h6>
                      </div>
                    </form>
        </form>

    </div>


  );



}





export default App;
