import { waitForElementToBeRemoved } from '@testing-library/dom';
import { computeHeadingLevel } from '@testing-library/dom';
import React, { useState } from 'react';
import ReactDOM from 'react-dom';

import './App.css';




function App() {

  const [name, setName] = useState([]);
  const [ele, setEle] = useState('');


  function keyChange(e) {
    setEle(e.target.value);
  }

  function handleChange(event) {
    event.preventDefault();
    const namelist= [...name];
    namelist.push(ele);
    setName(namelist);
    setEle('');

 }

 function showDropdown() {
  ReactDOM.render(document.getElementById('myDropdown'));
}

  return (
    <div className="Tasklist">
       <form>
                <div className="header" >
                    <h3>Task list</h3>
                    <input type="text"
                           placeholder="Your Tasks"
                           value={ele}
                           onChange={keyChange}
                           />
                     <button type="submit" onClick={handleChange}>Add</button>
                 </div> 
                 <h6 className="heading_pending">Pending:</h6>
                 <form className="Pendingform">
                 <div className="body">
                      {
                         name.map(_el => {
                        return <ul className="card_pending">
                                  <div>
                                    <card>{_el}</card>
                                    <ul className="dropbtn icons btn-right showLeft" onClick={showDropdown()}>
                                        <li></li>
                                        <li></li>
                                        <li></li>
                                    </ul>
                                    <div id="myDropdown" className="dropdown-content">
                                        <a href="#View">View</a>
                                        <a href="#Edit">Edit</a>
                                        <a href="#Delete">Delete</a>
                                    </div>
                                   </div> 
                                </ul>;
                       })
                       }
                 </div>
                </form>
                <h6 className="heading_Completed">Completed:</h6>
                <form className="Completedform">
                 <div className="body">

                 </div>
                </form>
        </form>

    </div>


  );
}

export default App;
