import React, { useState } from 'react';
import './App.css';



function App() {

  const [name, setName] = useState([]);
  const [ele, setEle] = useState('');
  const handleSubmit = (e) => {
  e.preventDefault();
  let names = [...name];
  names.push(ele);
  setName(names);
  }
  const currElement = (e) => {
  setEle(e.target.value);
  }

  return (
    <div className="Tasklist">
       <form>
                <div className="header" >
                    <h1>Task list</h1>
                </div>
                <div className="body">
                    <div className="tasks">
                       <form onSubmit={handleSubmit}>
			                    <input type="text" placeholder="add name" onChange={currElement} />
			                    <button>Add</button>
			                </form>
                    </div>
                </div>
        </form>
    </div>

  );
}

export default App;
