import React, { useState, Children } from "react";
import './modal.css';

const [charleft, setCharleft] = useState('140');



function Modal(e) {
return(
    <form>
       <div className="modeldata">
          <form>
              <div className="taskname">
                <label>
                    Name: 
                </label>
                <div>{e.task}</div>
              </div>
              <div className="taskDesc">
                <label>
                    Description:
                </label>
                <div>
                <textArea
                    rows={6}
                    type="text"
                    maxLength="140"
                    required
                   // onChange={handleWordCount}
                />
                <p>{charleft}</p>
                 </div>
                
             </div>
          </form>
       </div>
    </form>
)
}


export default Modal;
